import React, { useState } from 'react';
import Values from 'values.js';
import SingleColor from './SingleColor';


const Color = () => {

    const[color,setColor]=useState('');
    const[error,setError]=useState(false);
    const[list,setList]=useState(new Value);

console.log(color);

const handleSubmit=(e)=>{
e.preventDefault();
try{
   
    let colors= new Values(color).all(10);
   setList(colors)

}catch(errorr){
    setError(true)
  
}

}

  return (
    <>
    <section className='container'>
      
      <h2>Color Generator</h2>
    
      <form className='formy' onSubmit={handleSubmit}>
      <input className='hedhi' type='color' style={{color:`${color}`}}></input>
      <input
       className={`${!error? 'search':'errorSearch'}`} 
       value={color} 
       onChange={(e)=>{
           setColor(e.target.value)
      }}
      placeholder='#f15025'
      
      />

      <button className='btn'
       style={{backgroundColor:`${color}`,
       color:'white'
    }}>
        Submit
        
        </button> 
      </form>

    </section>
     <section className='colors'>
      
{
    list.map((colr,colorIndex)=>{

return <SingleColor key={colorIndex} color={colr} index={colorIndex} hex={colr.hex} />


    })
}


     </section>
     </>
  );
}

export default Color;
